package xacxsdas;

class rotation {

public static void gugu(int dan) {
	

	for (int i = 1; i < 10; i++) {
		System.out.printf("%d X %d = %d\n", dan, i, dan * i);

	}
	
}	
public static void gugu2(int dan2) {
	


	for (int i = 1; i < 10; i++) {
		System.out.printf("%d X %d = %d\n", dan2, i, dan2 * i);
	}

}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		gugu(2);
		gugu(3);
		gugu(4);

		
	}
			
	
}
